import{_ as a,c,o as e,a4 as i}from"./chunks/framework.VzvPGsLx.js";const b=JSON.parse('{"title":"Blender 笔记01: 基本操作和编辑模式","description":"","frontmatter":{"title":"Blender 笔记01: 基本操作和编辑模式","catalog":true,"date":"2020-10-12T16:50:00.000Z","subtitle":null,"header-img":null,"tags":["3D","Blender"],"recommend":"blender"},"headers":[],"relativePath":"docs/frontend/3d/blender-01-basic.md","filePath":"docs/frontend/3d/blender-01-basic.md"}'),o={name:"docs/frontend/3d/blender-01-basic.md"},t=i('<h1 id="blender-笔记-01-基本操作和编辑模式" tabindex="-1">Blender 笔记 01: 基本操作和编辑模式 <a class="header-anchor" href="#blender-笔记-01-基本操作和编辑模式" aria-label="Permalink to &quot;Blender 笔记 01: 基本操作和编辑模式&quot;">​</a></h1><h2 id="参考教程" tabindex="-1">参考教程 <a class="header-anchor" href="#参考教程" aria-label="Permalink to &quot;参考教程&quot;">​</a></h2><p><a href="https://www.bilibili.com/video/BV1WD4y1o7TT" target="_blank" rel="noreferrer">https://www.bilibili.com/video/BV1WD4y1o7TT</a></p><h2 id="快捷键" tabindex="-1">快捷键 <a class="header-anchor" href="#快捷键" aria-label="Permalink to &quot;快捷键&quot;">​</a></h2><h3 id="视图切换" tabindex="-1">视图切换（~） <a class="header-anchor" href="#视图切换" aria-label="Permalink to &quot;视图切换（~）&quot;">​</a></h3><p>通过波浪键可以快速切换视图</p><h3 id="切换编辑模式-tab" tabindex="-1">切换编辑模式（TAB） <a class="header-anchor" href="#切换编辑模式-tab" aria-label="Permalink to &quot;切换编辑模式（TAB）&quot;">​</a></h3><h3 id="约束轴向-ctrl" tabindex="-1">约束轴向（CTRL） <a class="header-anchor" href="#约束轴向-ctrl" aria-label="Permalink to &quot;约束轴向（CTRL）&quot;">​</a></h3><h3 id="着色模式切换-z" tabindex="-1">着色模式切换（Z） <a class="header-anchor" href="#着色模式切换-z" aria-label="Permalink to &quot;着色模式切换（Z）&quot;">​</a></h3><p><img src="https://imgs-1251264059.cos.ap-chengdu.myqcloud.com/picgo-v2/a54638310c4e7d0257f987d5e6ec92f6.png" alt="image-20201018210815141"></p><h3 id="着色模式切换-线框模式-alt-z" tabindex="-1">着色模式切换（线框模式）（ALT+Z） <a class="header-anchor" href="#着色模式切换-线框模式-alt-z" aria-label="Permalink to &quot;着色模式切换（线框模式）（ALT+Z）&quot;">​</a></h3><h3 id="四视图-ctrl-alt-q" tabindex="-1">四视图（CTRL+ALT+Q） <a class="header-anchor" href="#四视图-ctrl-alt-q" aria-label="Permalink to &quot;四视图（CTRL+ALT+Q）&quot;">​</a></h3><h3 id="隐藏选中项-h" tabindex="-1">隐藏选中项（H） <a class="header-anchor" href="#隐藏选中项-h" aria-label="Permalink to &quot;隐藏选中项（H）&quot;">​</a></h3><h3 id="取消隐藏选中项-alt-h" tabindex="-1">取消隐藏选中项（ALT+H） <a class="header-anchor" href="#取消隐藏选中项-alt-h" aria-label="Permalink to &quot;取消隐藏选中项（ALT+H）&quot;">​</a></h3><h3 id="全选-a" tabindex="-1">全选（A） <a class="header-anchor" href="#全选-a" aria-label="Permalink to &quot;全选（A）&quot;">​</a></h3><h3 id="反选-ctrl-i" tabindex="-1">反选（CTRL+I） <a class="header-anchor" href="#反选-ctrl-i" aria-label="Permalink to &quot;反选（CTRL+I）&quot;">​</a></h3><h3 id="全选相关联的物体-ctrl-l" tabindex="-1">全选相关联的物体（CTRL+L） <a class="header-anchor" href="#全选相关联的物体-ctrl-l" aria-label="Permalink to &quot;全选相关联的物体（CTRL+L）&quot;">​</a></h3><h3 id="重置-3d-游标位置-shift-c" tabindex="-1">重置 3D 游标位置（SHIFT+C） <a class="header-anchor" href="#重置-3d-游标位置-shift-c" aria-label="Permalink to &quot;重置 3D 游标位置（SHIFT+C）&quot;">​</a></h3><h3 id="快速移动-3d-游标-shift-s" tabindex="-1">快速移动 3D 游标（SHIFT+S） <a class="header-anchor" href="#快速移动-3d-游标-shift-s" aria-label="Permalink to &quot;快速移动 3D 游标（SHIFT+S）&quot;">​</a></h3><p>选中物体后 SHIFT+S</p><h3 id="变换相关的快捷键" tabindex="-1">变换相关的快捷键 <a class="header-anchor" href="#变换相关的快捷键" aria-label="Permalink to &quot;变换相关的快捷键&quot;">​</a></h3><h4 id="移动-g-或-gx、gy、gz" tabindex="-1">移动：G 或 GX、GY、GZ <a class="header-anchor" href="#移动-g-或-gx、gy、gz" aria-label="Permalink to &quot;移动：G 或 GX、GY、GZ&quot;">​</a></h4><h4 id="缩放-s-或-sx、sy、sz" tabindex="-1">缩放：S 或 SX、SY、SZ <a class="header-anchor" href="#缩放-s-或-sx、sy、sz" aria-label="Permalink to &quot;缩放：S 或 SX、SY、SZ&quot;">​</a></h4><h4 id="旋转-r-或-rx、ry、rz" tabindex="-1">旋转：R 或 RX、RY、RZ <a class="header-anchor" href="#旋转-r-或-rx、ry、rz" aria-label="Permalink to &quot;旋转：R 或 RX、RY、RZ&quot;">​</a></h4><h4 id="展示数字化信息-n" tabindex="-1">展示数字化信息：N <a class="header-anchor" href="#展示数字化信息-n" aria-label="Permalink to &quot;展示数字化信息：N&quot;">​</a></h4><h3 id="新建物体-shift-a" tabindex="-1">新建物体（SHIFT+A） <a class="header-anchor" href="#新建物体-shift-a" aria-label="Permalink to &quot;新建物体（SHIFT+A）&quot;">​</a></h3><p><img src="https://imgs-1251264059.cos.ap-chengdu.myqcloud.com/picgo-v2/859c4d4a7ff1dd39bcf9d562fd1dacda.png" alt="image-20201018192221219"></p><h3 id="删除物体-x" tabindex="-1">删除物体（X） <a class="header-anchor" href="#删除物体-x" aria-label="Permalink to &quot;删除物体（X）&quot;">​</a></h3><p><img src="https://imgs-1251264059.cos.ap-chengdu.myqcloud.com/picgo-v2/690ea162db1f7283aa8b7f1f8191905d.png" alt="image-20201018192143201"></p><h3 id="复制所选-shift-d" tabindex="-1">复制所选（SHIFT+D） <a class="header-anchor" href="#复制所选-shift-d" aria-label="Permalink to &quot;复制所选（SHIFT+D）&quot;">​</a></h3><h3 id="循环选择-alt-左键-ctrl-alt-左键" tabindex="-1">循环选择（ALT+左键/CTRL+ALT+左键） <a class="header-anchor" href="#循环选择-alt-左键-ctrl-alt-左键" aria-label="Permalink to &quot;循环选择（ALT+左键/CTRL+ALT+左键）&quot;">​</a></h3><p>循环边：ALT+左键</p><p><img src="https://imgs-1251264059.cos.ap-chengdu.myqcloud.com/picgo-v2/eaab2e666bdaedb0cf1b6a9803a447bb.png" alt="image-20201018145732810"></p><p>竖向循环：CTRL+ALT+左键</p><p><img src="https://imgs-1251264059.cos.ap-chengdu.myqcloud.com/picgo-v2/ab6ce37b0535c6e5c84297ab2f19a121.png" alt="image-20201018145713855"></p><p>间隔选：</p><p><img src="https://imgs-1251264059.cos.ap-chengdu.myqcloud.com/picgo-v2/b00e53dd10adc52841eaf5f8bc8762c8.png" alt="image-20201018150012921"></p><p><img src="https://imgs-1251264059.cos.ap-chengdu.myqcloud.com/picgo-v2/5672d6e4aef62bef930b8b56a04f066b.png" alt="image-20201018150115922"></p><h3 id="改键-用空格快速切换工具" tabindex="-1">改键：用空格快速切换工具 <a class="header-anchor" href="#改键-用空格快速切换工具" aria-label="Permalink to &quot;改键：用空格快速切换工具&quot;">​</a></h3><p><img src="https://imgs-1251264059.cos.ap-chengdu.myqcloud.com/picgo-v2/ff477417b4639b4e9c15abf5d7d382f6.png" alt="image-20201018150430818"></p><p><img src="https://imgs-1251264059.cos.ap-chengdu.myqcloud.com/picgo-v2/b8fce498ba56097f32a3554e4eeb540b.png" alt="image-20201018150440145"></p><h2 id="挤出工具-e" tabindex="-1">挤出工具（E） <a class="header-anchor" href="#挤出工具-e" aria-label="Permalink to &quot;挤出工具（E）&quot;">​</a></h2><p><img src="https://imgs-1251264059.cos.ap-chengdu.myqcloud.com/picgo-v2/f76ead6db6bceb4352b4cbd64f05d100.png" alt="image-20201018150754761"></p><h3 id="挤出选区" tabindex="-1">挤出选区 <a class="header-anchor" href="#挤出选区" aria-label="Permalink to &quot;挤出选区&quot;">​</a></h3><p><img src="https://imgs-1251264059.cos.ap-chengdu.myqcloud.com/picgo-v2/a16c59ae94af9f243ec01ee481308f61.png" alt="image-20201018150803820"></p><h3 id="法线向挤出" tabindex="-1">法线向挤出 <a class="header-anchor" href="#法线向挤出" aria-label="Permalink to &quot;法线向挤出&quot;">​</a></h3><p><img src="https://imgs-1251264059.cos.ap-chengdu.myqcloud.com/picgo-v2/ef53f2819fcb2bf10c0066217d9f527a.png" alt="image-20201018151008081"></p><h3 id="挤出各个面" tabindex="-1">挤出各个面 <a class="header-anchor" href="#挤出各个面" aria-label="Permalink to &quot;挤出各个面&quot;">​</a></h3><p><img src="https://imgs-1251264059.cos.ap-chengdu.myqcloud.com/picgo-v2/159351a88c4c7083001e1486bddf3fc3.png" alt="image-20201018151032110"></p><h3 id="挤出到光标-鼠标位置" tabindex="-1">挤出到光标（鼠标位置） <a class="header-anchor" href="#挤出到光标-鼠标位置" aria-label="Permalink to &quot;挤出到光标（鼠标位置）&quot;">​</a></h3><p><img src="https://imgs-1251264059.cos.ap-chengdu.myqcloud.com/picgo-v2/f0e0e392ad5b2e72ab4a8f66fb01c5d0.png" alt="image-20201018151116185"></p><h3 id="挤出流形-自动融并面" tabindex="-1">挤出流形（自动融并面） <a class="header-anchor" href="#挤出流形-自动融并面" aria-label="Permalink to &quot;挤出流形（自动融并面）&quot;">​</a></h3><p><img src="https://imgs-1251264059.cos.ap-chengdu.myqcloud.com/picgo-v2/079b5dda0c8222ef684a397fa624c661.png" alt="image-20201018151706416"></p><h2 id="内插面工具-i" tabindex="-1">内插面工具（I） <a class="header-anchor" href="#内插面工具-i" aria-label="Permalink to &quot;内插面工具（I）&quot;">​</a></h2><p><img src="https://imgs-1251264059.cos.ap-chengdu.myqcloud.com/picgo-v2/85f8dbf4033b7777119fc8b3a38386dd.png" alt="image-20201018193145125"></p><p><img src="https://imgs-1251264059.cos.ap-chengdu.myqcloud.com/picgo-v2/a0099bb4b78a82cd2182416f94d8882b.png" alt="image-20201018185242459"></p><h2 id="倒角工具-ctrl-b" tabindex="-1">倒角工具（CTRL+B） <a class="header-anchor" href="#倒角工具-ctrl-b" aria-label="Permalink to &quot;倒角工具（CTRL+B）&quot;">​</a></h2><p><img src="https://imgs-1251264059.cos.ap-chengdu.myqcloud.com/picgo-v2/c241d5905a25fbf82336b32c362775d3.png" alt="image-20201018193137650"></p><p><img src="https://imgs-1251264059.cos.ap-chengdu.myqcloud.com/picgo-v2/6654ec66a9836b8541d68044e6b2eb2f.png" alt="image-20201018185423327"></p><h2 id="环切工具-ctrl-r" tabindex="-1">环切工具（CTRL+R） <a class="header-anchor" href="#环切工具-ctrl-r" aria-label="Permalink to &quot;环切工具（CTRL+R）&quot;">​</a></h2><p><img src="https://imgs-1251264059.cos.ap-chengdu.myqcloud.com/picgo-v2/23fbc24bc2dfdd72bed0ef510a7a20b6.png" alt="image-20201018193128861"></p><p><img src="https://imgs-1251264059.cos.ap-chengdu.myqcloud.com/picgo-v2/dab741ebb3bbf83fec25a7b4a1ac4866.png" alt="image-20201018185914531"></p><h2 id="切割工具-k-穿透切割-kz" tabindex="-1">切割工具（K）（穿透切割 KZ） <a class="header-anchor" href="#切割工具-k-穿透切割-kz" aria-label="Permalink to &quot;切割工具（K）（穿透切割 KZ）&quot;">​</a></h2><p><img src="https://imgs-1251264059.cos.ap-chengdu.myqcloud.com/picgo-v2/51649105b9b774c22ae52dceaa35843b.png" alt="image-20201018193118986"></p><p><img src="https://imgs-1251264059.cos.ap-chengdu.myqcloud.com/picgo-v2/fa378a08e1b00bc296385dfe90768a8b.png" alt="image-20201018190512717"></p><h2 id="多边形建型工具" tabindex="-1">多边形建型工具 <a class="header-anchor" href="#多边形建型工具" aria-label="Permalink to &quot;多边形建型工具&quot;">​</a></h2><p><img src="https://imgs-1251264059.cos.ap-chengdu.myqcloud.com/picgo-v2/23183c06e9ce9e39b2bfbc2e31036a52.png" alt="image-20201018193016544"></p><p><img src="https://imgs-1251264059.cos.ap-chengdu.myqcloud.com/picgo-v2/e64cd779cc5806a48347ea15feb07f9b.png" alt="image-20201018190907117"></p><p>按住 CTRL 可以吸附</p><h2 id="旋绕工具" tabindex="-1">旋绕工具 <a class="header-anchor" href="#旋绕工具" aria-label="Permalink to &quot;旋绕工具&quot;">​</a></h2><p><img src="https://imgs-1251264059.cos.ap-chengdu.myqcloud.com/picgo-v2/42d372d693e495a8cd28e8b03acadc81.png" alt="image-20201018193025875"></p><p><img src="https://imgs-1251264059.cos.ap-chengdu.myqcloud.com/picgo-v2/c0bf70874baf64fb760f53b162f6e984.png" alt="image-20201018191539111"></p><p>注意：是以 3D 游标为中心操作的</p><p><img src="https://imgs-1251264059.cos.ap-chengdu.myqcloud.com/picgo-v2/a85da3207b01e73729a6ade1970045a8.png" alt="image-20201018191842166"></p><p>使用副本的效果</p><h2 id="细分-右键菜单" tabindex="-1">细分（右键菜单） <a class="header-anchor" href="#细分-右键菜单" aria-label="Permalink to &quot;细分（右键菜单）&quot;">​</a></h2><p>右键需要细分的物体</p><h2 id="光滑工具" tabindex="-1">光滑工具 <a class="header-anchor" href="#光滑工具" aria-label="Permalink to &quot;光滑工具&quot;">​</a></h2><p><img src="https://imgs-1251264059.cos.ap-chengdu.myqcloud.com/picgo-v2/bd6b4d58fc1be1ad58cda205ab5fc7f3.png" alt="image-20201018192934683"></p><p>点击黄点，可上下拖动、左右拖动</p><p><img src="https://imgs-1251264059.cos.ap-chengdu.myqcloud.com/picgo-v2/a106f59e94ba18611c7ed61500525e2e.png" alt="image-20201018192641448"></p><h2 id="随机工具" tabindex="-1">随机工具 <a class="header-anchor" href="#随机工具" aria-label="Permalink to &quot;随机工具&quot;">​</a></h2><p><img src="https://imgs-1251264059.cos.ap-chengdu.myqcloud.com/picgo-v2/bd6b4d58fc1be1ad58cda205ab5fc7f3.png" alt="image-20201018192934683"></p><p><img src="https://imgs-1251264059.cos.ap-chengdu.myqcloud.com/picgo-v2/d2ca32b4a71efc954e3d832be97d66fe.png" alt="image-20201018192735289"></p><p><img src="https://imgs-1251264059.cos.ap-chengdu.myqcloud.com/picgo-v2/b790df3ef121187db746a6fef43832fc.png" alt="image-20201018192834517"></p><h2 id="滑移边线-顶点-工具" tabindex="-1">滑移边线（顶点）工具 <a class="header-anchor" href="#滑移边线-顶点-工具" aria-label="Permalink to &quot;滑移边线（顶点）工具&quot;">​</a></h2><p><img src="https://imgs-1251264059.cos.ap-chengdu.myqcloud.com/picgo-v2/806c0f4311e8f83e51727edd3b6b518d.png" alt="image-20201018193352209"></p><p><img src="https://imgs-1251264059.cos.ap-chengdu.myqcloud.com/picgo-v2/6148610ea7fc47cf2c9e62de02f1f546.png" alt="image-20201018193341541"></p><p><img src="https://imgs-1251264059.cos.ap-chengdu.myqcloud.com/picgo-v2/72b7b525e44d2e78314849a994a0937c.png" alt="image-20201018193443073"></p><p><img src="https://imgs-1251264059.cos.ap-chengdu.myqcloud.com/picgo-v2/a558f751f1ddde2ec0ef5f42f4c1bcae.png" alt="image-20201018193508572"></p><h2 id="法向缩放-alt-s-和推拉工具" tabindex="-1">法向缩放（ALT+S）和推拉工具 <a class="header-anchor" href="#法向缩放-alt-s-和推拉工具" aria-label="Permalink to &quot;法向缩放（ALT+S）和推拉工具&quot;">​</a></h2><p><img src="https://imgs-1251264059.cos.ap-chengdu.myqcloud.com/picgo-v2/d667ea4cd9a3cb97e837d245f24b9170.png" alt="image-20201018194729477"></p><p><img src="https://imgs-1251264059.cos.ap-chengdu.myqcloud.com/picgo-v2/7dfe38b8b89b119cdf7fa7afaab3c1a3.png" alt="image-20201018194436393"></p><p>上侧为普通 S 缩放，下侧为 ALT+S 法向缩放</p><p>推拉工具可配合游标使用</p><p><img src="https://imgs-1251264059.cos.ap-chengdu.myqcloud.com/picgo-v2/89b887f480564d6228984ecd390bc856.png" alt="image-20201018194614061"></p><p><img src="https://imgs-1251264059.cos.ap-chengdu.myqcloud.com/picgo-v2/cb726bb865fca96d2294c50dcd2012ac.png" alt="image-20201018194553147"></p><h2 id="斜切工具" tabindex="-1">斜切工具 <a class="header-anchor" href="#斜切工具" aria-label="Permalink to &quot;斜切工具&quot;">​</a></h2><p><img src="https://imgs-1251264059.cos.ap-chengdu.myqcloud.com/picgo-v2/90819651d6c4de4fbb8f13095b94da5f.png" alt="image-20201018194745900"></p><p><img src="https://imgs-1251264059.cos.ap-chengdu.myqcloud.com/picgo-v2/be9b84b527a4d01c1a4021b528dbb63e.png" alt="image-20201018194812394"></p><p><img src="https://imgs-1251264059.cos.ap-chengdu.myqcloud.com/picgo-v2/a7ca298e5243f4569da615e792b97d28.png" alt="image-20201018194836882"></p><h2 id="断离工具" tabindex="-1">断离工具 <a class="header-anchor" href="#断离工具" aria-label="Permalink to &quot;断离工具&quot;">​</a></h2><p><img src="https://imgs-1251264059.cos.ap-chengdu.myqcloud.com/picgo-v2/9f3faccd083fedc16274e6d653698a95.png" alt="image-20201018194934480"></p><p><img src="https://imgs-1251264059.cos.ap-chengdu.myqcloud.com/picgo-v2/510f1cc8a572a6c7a8460671b46f850d.png" alt="image-20201018195022540"></p><h2 id="合并-m-和拆分-alt-m-工具" tabindex="-1">合并（M）和拆分（ALT+M）工具 <a class="header-anchor" href="#合并-m-和拆分-alt-m-工具" aria-label="Permalink to &quot;合并（M）和拆分（ALT+M）工具&quot;">​</a></h2><p><img src="https://imgs-1251264059.cos.ap-chengdu.myqcloud.com/picgo-v2/61cee86db978107368ce1166e36501ab.png" alt="image-20201018195258244"></p><p><img src="https://imgs-1251264059.cos.ap-chengdu.myqcloud.com/picgo-v2/ec0317c30cdb996fc568aff9c244ab90.png" alt="image-20201018195353838"></p><h2 id="分离工具-p" tabindex="-1">分离工具（P） <a class="header-anchor" href="#分离工具-p" aria-label="Permalink to &quot;分离工具（P）&quot;">​</a></h2><p>与拆分工具类似，但会创建新的物体。</p><p><img src="https://imgs-1251264059.cos.ap-chengdu.myqcloud.com/picgo-v2/7c3dd06bc639fed8cd44c08e1b38d154.png" alt="image-20201018203247006"></p><h2 id="投影切割工具" tabindex="-1">投影切割工具 <a class="header-anchor" href="#投影切割工具" aria-label="Permalink to &quot;投影切割工具&quot;">​</a></h2><p>先用 SHIFT 选择 2 个物体</p><p><img src="https://imgs-1251264059.cos.ap-chengdu.myqcloud.com/picgo-v2/5db919f85f160a41e7949858918e9cd5.png" alt="image-20201018203430649"></p><p><img src="https://imgs-1251264059.cos.ap-chengdu.myqcloud.com/picgo-v2/3972381c37b337dbb30a5ae873740287.png" alt="image-20201018203447616"></p><h2 id="从顶点创建边面工具-f" tabindex="-1">从顶点创建边面工具（F） <a class="header-anchor" href="#从顶点创建边面工具-f" aria-label="Permalink to &quot;从顶点创建边面工具（F）&quot;">​</a></h2><p><img src="https://imgs-1251264059.cos.ap-chengdu.myqcloud.com/picgo-v2/43232dab8a00057508b51e03268b723e.png" alt="image-20201018203831587"></p><p><img src="https://imgs-1251264059.cos.ap-chengdu.myqcloud.com/picgo-v2/2b4edce99931ddf8b3b18e82da8a119f.png" alt="image-20201018203942608"></p><p><img src="https://imgs-1251264059.cos.ap-chengdu.myqcloud.com/picgo-v2/a819a28174ac3ac5b93190b2aa60f296.png" alt="image-20201018203949028"></p><h2 id="链接顶点路径-j" tabindex="-1">链接顶点路径（J） <a class="header-anchor" href="#链接顶点路径-j" aria-label="Permalink to &quot;链接顶点路径（J）&quot;">​</a></h2><p><img src="https://imgs-1251264059.cos.ap-chengdu.myqcloud.com/picgo-v2/23ac4a3b0e0d3cc4ddaa74be8ede1e02.png" alt="image-20201018204130232"></p><p>需要先选择两个顶点，然后 J，可以链接顶点，并切割当前的面</p><p><img src="https://imgs-1251264059.cos.ap-chengdu.myqcloud.com/picgo-v2/80652797edeb5510e04ce7c83fd395ea.png" alt="image-20201018204318295"></p><h2 id="断离顶点-v" tabindex="-1">断离顶点（V） <a class="header-anchor" href="#断离顶点-v" aria-label="Permalink to &quot;断离顶点（V）&quot;">​</a></h2><p><img src="https://imgs-1251264059.cos.ap-chengdu.myqcloud.com/picgo-v2/bef62c2c27139c99a48b1190442d2ab0.png" alt="image-20201018204510235"></p><p><img src="https://imgs-1251264059.cos.ap-chengdu.myqcloud.com/picgo-v2/1ff71b35ad4f902a439ffcf62cd5c6ec.png" alt="image-20201018204522618"></p><h2 id="桥接循环边" tabindex="-1">桥接循环边 <a class="header-anchor" href="#桥接循环边" aria-label="Permalink to &quot;桥接循环边&quot;">​</a></h2><p><img src="https://imgs-1251264059.cos.ap-chengdu.myqcloud.com/picgo-v2/7b429d325f80ec151d64b2ce6a99c6fa.png" alt="image-20201018205109462"></p><p><img src="https://imgs-1251264059.cos.ap-chengdu.myqcloud.com/picgo-v2/203adf42278830ea2cd59e0a00c6cdb4.png" alt="image-20201018205040542"></p><p><img src="https://imgs-1251264059.cos.ap-chengdu.myqcloud.com/picgo-v2/033e3778465331a17a8d27a326c5cf2d.png" alt="image-20201018205051849"></p><h2 id="边线折痕-shift-e" tabindex="-1">边线折痕（SHIFT+E） <a class="header-anchor" href="#边线折痕-shift-e" aria-label="Permalink to &quot;边线折痕（SHIFT+E）&quot;">​</a></h2><p><img src="https://imgs-1251264059.cos.ap-chengdu.myqcloud.com/picgo-v2/afc19d762021ed0b74291f88f27cba2f.png" alt="image-20201018205446247"></p><p>需要先用「修改器：表面细分」后演示</p><p><img src="https://imgs-1251264059.cos.ap-chengdu.myqcloud.com/picgo-v2/19bcdc7889459772fa460ae3b6323d36.png" alt="image-20201018205401160"></p><p><img src="https://imgs-1251264059.cos.ap-chengdu.myqcloud.com/picgo-v2/7c121943dc22b8fb8143b6fb6de1f282.png" alt="image-20201018205420519"></p><p><img src="https://imgs-1251264059.cos.ap-chengdu.myqcloud.com/picgo-v2/4337344c5dcdca1c9d89fb8b0a939f62.png" alt="image-20201018205434486"></p><h2 id="交集切割" tabindex="-1">交集切割 <a class="header-anchor" href="#交集切割" aria-label="Permalink to &quot;交集切割&quot;">​</a></h2><p><img src="https://imgs-1251264059.cos.ap-chengdu.myqcloud.com/picgo-v2/9281fd0c23dc00d6a4e34c2ddfdb44ab.png" alt="image-20201018210132109"></p><p><img src="https://imgs-1251264059.cos.ap-chengdu.myqcloud.com/picgo-v2/def27cb95769721e508b869011833c9a.png" alt="image-20201018210101794"></p><p><img src="https://imgs-1251264059.cos.ap-chengdu.myqcloud.com/picgo-v2/309b0e01497bb5680703a505b7f70dfa.png" alt="image-20201018210114793"></p><h2 id="交集布尔" tabindex="-1">交集布尔 <a class="header-anchor" href="#交集布尔" aria-label="Permalink to &quot;交集布尔&quot;">​</a></h2><p><img src="https://imgs-1251264059.cos.ap-chengdu.myqcloud.com/picgo-v2/cf9a0681ad6f3d98081868f94c32e8b1.png" alt="image-20201018210314841"></p><h2 id="面实体化" tabindex="-1">面实体化 <a class="header-anchor" href="#面实体化" aria-label="Permalink to &quot;面实体化&quot;">​</a></h2><p>可以用于给面添加厚度</p><p><img src="https://imgs-1251264059.cos.ap-chengdu.myqcloud.com/picgo-v2/f56aa5d28a99c7126f0029044cfe6fde.png" alt="image-20201018211043999"></p><p><img src="https://imgs-1251264059.cos.ap-chengdu.myqcloud.com/picgo-v2/6fce2e3138ff07367dd83bff0168bf28.png" alt="image-20201018210924710"></p><p><img src="https://imgs-1251264059.cos.ap-chengdu.myqcloud.com/picgo-v2/3dbf7c0beb8539dced321c3f0c0fa31f.png" alt="image-20201018210939263"></p><h2 id="填充-alt-f-、栅格填充" tabindex="-1">填充（ALT+F）、栅格填充 <a class="header-anchor" href="#填充-alt-f-、栅格填充" aria-label="Permalink to &quot;填充（ALT+F）、栅格填充&quot;">​</a></h2><p><img src="https://imgs-1251264059.cos.ap-chengdu.myqcloud.com/picgo-v2/361762c91ffee8ffacbebaa43dd124a6.png" alt="image-20201018211429974"></p><p>填充</p><p><img src="https://imgs-1251264059.cos.ap-chengdu.myqcloud.com/picgo-v2/bde6cf77c06137af19411a79e5a2bbe4.png" alt="image-20201018211339925"></p><p>栅格填充</p><p><img src="https://imgs-1251264059.cos.ap-chengdu.myqcloud.com/picgo-v2/73cd868a00c8866f12b85fc7c7ef3199.png" alt="image-20201018211356381"></p><h2 id="物体应用调整工具-ctrl-a" tabindex="-1">物体应用调整工具（CTRL+A） <a class="header-anchor" href="#物体应用调整工具-ctrl-a" aria-label="Permalink to &quot;物体应用调整工具（CTRL+A）&quot;">​</a></h2><p>用于解决物体模式下的缩放与编辑模式下的缩放不统一的问题</p><p><img src="https://imgs-1251264059.cos.ap-chengdu.myqcloud.com/picgo-v2/56d5b0361805fc1025c943ea89c068c0.png" alt="image-20201018211747164"></p><h2 id="原点和父级工具" tabindex="-1">原点和父级工具 <a class="header-anchor" href="#原点和父级工具" aria-label="Permalink to &quot;原点和父级工具&quot;">​</a></h2><h3 id="设置原点-影响变换的参考点" tabindex="-1">设置原点（影响变换的参考点） <a class="header-anchor" href="#设置原点-影响变换的参考点" aria-label="Permalink to &quot;设置原点（影响变换的参考点）&quot;">​</a></h3><p><img src="https://imgs-1251264059.cos.ap-chengdu.myqcloud.com/picgo-v2/1e65c4c417331a5e495e21938bd7ec8b.png" alt="image-20201018212005224"></p><h3 id="父级-ctrl-p" tabindex="-1">父级（CTRL+P） <a class="header-anchor" href="#父级-ctrl-p" aria-label="Permalink to &quot;父级（CTRL+P）&quot;">​</a></h3><p>最后一个选的是父级，缩放等变换时父子会同时变换</p><p><img src="https://imgs-1251264059.cos.ap-chengdu.myqcloud.com/picgo-v2/c46b5ba8c6c42c63fa311ff9f62fef97.png" alt="image-20201018212210169"></p><p><img src="https://imgs-1251264059.cos.ap-chengdu.myqcloud.com/picgo-v2/e8717f84969e7ac076a13731a48bb0da.png" alt="image-20201018212251772"></p>',162),d=[t];function p(s,g,h,m,l,r){return e(),c("div",null,d)}const u=a(o,[["render",p]]);export{b as __pageData,u as default};
