import{_ as e,c as a,o as s,a4 as t}from"./chunks/framework.CWaimgAe.js";const _=JSON.parse('{"title":"Linux CheckInstall 命令使用指南","description":"","frontmatter":{"title":"Linux CheckInstall 命令使用指南","catalog":true,"date":"2018-10-30T14:45:00.000Z","subtitle":null,"header-img":null,"tags":["Linux"]},"headers":[],"relativePath":"docs/os/linux-checkinstall-note.md","filePath":"docs/os/linux-checkinstall-note.md"}'),c={name:"docs/os/linux-checkinstall-note.md"},l=t(`<h1 id="linux-checkinstall-命令使用指南" tabindex="-1">Linux CheckInstall 命令使用指南 <a class="header-anchor" href="#linux-checkinstall-命令使用指南" aria-label="Permalink to &quot;Linux CheckInstall 命令使用指南&quot;">​</a></h1><blockquote><p><a href="http://asic-linux.com.mx/~izto/checkinstall/" target="_blank" rel="noreferrer">http://asic-linux.com.mx/~izto/checkinstall/</a></p></blockquote><p>CheckInstall 能跟踪由 <code>make install</code> 或类似命令安装的所有文件，并为这些文件创建 Slackware、RPM 或者 Debian 安装包，然后把它添加到已安装软件包数据库中，以便能简便的卸载或发布安装包。使用 CheckInstall 而不是仅仅运行 <code>sudo make install</code>，因为那多半会把文件放在文件系统的很多地方，万一出错了可没有容易的方法删除它们。如果在将来你试图安装一个含有和你现在编译的文件相同的文件的软件包，你将收到出错消息，你编译好的软件可能停止工作。</p><p>安装</p><p><code>sudo apt-get install checkinstall</code></p><p>使用</p><p>你将使用 <code>sudo checkinstall</code> 代替 <code>sudo make install</code> 。</p><p>那安装了的软件包之后也可通过 新立得 (synaptic) 或用下列语句很容易地卸载。</p><p><code>sudo dpkg -r packagename</code></p><p>通过 <code>auto-apt</code> 使用 <code>CheckInstall</code></p><p>当你想用 <code>checkinstall</code> 从源码建立一个简单的软件包，你可以使用 <code>auto-apt</code> 。你需要安装 <code>auto-apt</code> ！</p><p>代替：</p><p><code>./configure</code></p><p>你可使用：</p><p><code>auto-apt run ./configure</code></p><p>如果有可用的依赖包，会弹出一个对话框，让你安装他们。</p><p>接着的步骤就一样了：</p><div class="language-sh vp-adaptive-theme line-numbers-mode"><button title="Copy Code" class="copy"></button><span class="lang">sh</span><pre class="shiki shiki-themes github-light github-dark vp-code"><code><span class="line"><span style="--shiki-light:#6F42C1;--shiki-dark:#B392F0;">make</span></span>
<span class="line"><span style="--shiki-light:#6F42C1;--shiki-dark:#B392F0;">sudo</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;"> checkinstall</span></span></code></pre><div class="line-numbers-wrapper" aria-hidden="true"><span class="line-number">1</span><br><span class="line-number">2</span><br></div></div>`,18),n=[l];function i(o,p,d,r,h,u){return s(),a("div",null,n)}const m=e(c,[["render",i]]);export{_ as __pageData,m as default};
